{
    "query": "
    query getChargingHistoryV2($pageNumber: Int!, $sortBy: String, $sortOrder: SortByEnum) {
  me {
    charging {
      historyV2(pageNumber: $pageNumber, sortBy: $sortBy, sortOrder: $sortOrder) {
        data {
          ...SparkHistoryItemFragment
        }
        totalResults
        hasMoreData
        pageNumber
      }
    }
  }
}

    fragment SparkHistoryItemFragment on SparkHistoryItem {
  countryCode
  programType
  billingType
  vin
  isMsp
  credit {
    distance
    distanceUnit
  }
  chargingPackage {
    distance
    distanceUnit
    energyApplied
  }
  chargingVoucher {
    voucherValue
  }
  invoices {
    fileName
    contentId
    invoiceType
  }
  chargeSessionId
  siteLocationName
  chargeStartDateTime
  chargeStopDateTime
  unlatchDateTime
  fees {
    ...SparkHistoryFeeFragment
  }
  vehicleMakeType
  sessionId
  surveyCompleted
  surveyType
  postId
  cabinetId
  din
  isDcEnforced
}

    fragment SparkHistoryFeeFragment on SparkHistoryFee {
  sessionFeeId
  feeType
  payorUid
  amountDue
  currencyCode
  pricingType
  usageBase
  usageTier1
  usageTier2
  usageTier3
  usageTier4
  rateBase
  rateTier1
  rateTier2
  rateTier3
  rateTier4
  totalTier1
  totalTier2
  totalTier3
  totalTier4
  uom
  isPaid
  uid
  totalBase
  totalDue
  netDue
  status
}
    ",
    "variables": {
        "sortBy": "start_datetime",
        "sortOrder": "DESC",
        "pageNumber": 1
    },
    "operationName": "getChargingHistoryV2"
}
